import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const currentDate = new Date().toLocaleDateString();
  const today = new Date();
  const nextDay = new Date(today);
  nextDay.setDate(today.getDate() + 1);

  const [formValues, setFormValues] = useState([]);

  useEffect(() => {
    // Fetch data from API
    const fetchData = async () => {
      try {
        const response = await axios.get('YOUR_API_URL'); // Replace 'YOUR_API_URL' with the actual API endpoint
        const apiData = response.data;

        // Update formValues with data from the API
        const updatedFormValues = apiData.map((item) => ({
          Note: item.note,
          DueDate: item.dueDate, // Assuming the API returns dueDate
          Date: item.date,
        }));

        setFormValues(updatedFormValues);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); // Call the fetchData function
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const handleChange = (i, e) => {
    let newFormValues = [...formValues];
    newFormValues[i][e.target.name] = e.target.value;
    setFormValues(newFormValues);
  };

  const addFormFields = () => {
    setFormValues([...formValues, { Note: "", DueDate: currentDate, Date: currentDate }]);
  };

  const handleRemoveField = (i) => {
    let newFormValues = [...formValues];
    newFormValues.splice(i, 1);
    setFormValues(newFormValues);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your logic to submit data, if needed
  };

  return (
    <form onSubmit={handleSubmit}>
      <h1><font size="+8">React Scribe</font></h1>
      <div className="span">
        <span>
          <button type="button" onClick={() => addFormFields()}>📝</button>
          <button type="button">Feeling Lucky</button>
        </span>
      </div>
      <div className="span">
        <span></span>
      </div>
      <div className="notes-container">
        {formValues.map((element, index) => (
          <div className="note-container" key={index}>
            <label htmlFor={`textbox-${index}`}><strong><font size="+3">  </font></strong></label>
            <p>{element.Date}</p>
            <textarea
              placeholder="Script destiny here! ✒️💀"
              cols="30"
              rows="10"
              id={`textbox-${index}`}
              type="text"
              name="Note"
              value={element.Note}
              onChange={(e) => handleChange(index, e)}
              key={`textarea-${index}`}
            ></textarea>
            <label htmlFor={`dueDate-${index}`}><strong>Due Date</strong></label>
            <input
              type="date"
              id={`dueDate-${index}`}
              name="DueDate"
              value={element.DueDate}
              onChange={(e) => handleChange(index, e)}
              style={{
                borderWidth: "3px",
                borderStyle:
                  new Date().getTime() >= new Date(element.DueDate).getTime()
                    ? "dotted"
                    : "none",
                borderColor: '#F4442E',
              }}
            />
            {index ? (
              <button type="button" onClick={() => handleRemoveField(index)}>🗑️</button>
            ) : null}
          </div>
        ))}
      </div>
      <button type="submit">Save Notes</button>
    </form>
  );
};

export default App;
