import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api', { withCredentials: true })
      .then(response => {
        const data = response.data;
        console.log(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const currentDate = new Date().toLocaleDateString();
  const today = new Date();
  const nextDay = new Date(today);
  nextDay.setDate(today.getDate() + 1);

  const initialFormValues = JSON.parse(localStorage.getItem('formValues')) || [
    { Note: "", DueDate: nextDay.toISOString().split('T')[0], Date: currentDate },
    { Note: "", DueDate: nextDay.toISOString().split('T')[0], Date: currentDate },
    { Note: "", DueDate: nextDay.toISOString().split('T')[0], Date: currentDate },
  ];

  const [formValues, setFormValues] = useState(initialFormValues);

  useEffect(() => {
    localStorage.setItem('formValues', JSON.stringify(formValues));
  }, [formValues, currentDate]);

  const handleChange = (i, e) => {
    let newFormValues = [...formValues];
    newFormValues[i][e.target.name] = e.target.value;
    setFormValues(newFormValues);
  };

  const addFormFields = () => {
    setFormValues([...formValues, { Note: "", DueDate: currentDate, Date: currentDate }]);
  };

  const handleRemoveField = (i) => {
    let newFormValues = [...formValues];
    newFormValues.splice(i, 1);
    setFormValues(newFormValues);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      for (const element of formValues) {
        await axios.post('http://127.0.0.1:8000/api', {
          "notes": element.Note,
          "due_date": element.DueDate,
          "date": element.Date
        });
      }
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h1><font size="+8">React Scribe</font></h1>
      <div className="span">
        <span>
          <button type="button" onClick={() => addFormFields()}>üìù</button>
          <button type="button">Feeling Lucky</button>
        </span>
      </div>
      <div className="span">
        <span></span>
      </div>
      <div className="notes-container">
        {formValues.map((element, index) => (
          <div className="note-container" key={index}>
            <label htmlFor={`textbox-${index}`}><strong><font size="+3">  </font></strong></label>
            <p>{element.Date}</p>
            <textarea
              placeholder="Script destiny here! ‚úíÔ∏èüíÄ"
              cols="30"
              rows="10"
              id={`textbox-${index}`}
              type="text"
              name="Note"
              value={element.Note}
              onChange={(e) => handleChange(index, e)}
              key={`textarea-${index}`}
            ></textarea>
            <label htmlFor={`dueDate-${index}`}><strong>Due Date</strong></label>
            <input
              type="date"
              id={`dueDate-${index}`}
              name="DueDate"
              value={element.DueDate}
              onChange={(e) => handleChange(index, e)}
              style={{
                borderWidth: "3px",
                borderStyle:
                  new Date().getTime() >= new Date(element.DueDate).getTime()
                    ? "dotted"
                    : "none",
                borderColor: '#F4442E',
              }}
            />
            {index ? (
              <button type="button" onClick={() => handleRemoveField(index)}>üóëÔ∏è</button>
            ) : null}
          </div>
        ))}
      </div>
      <button type="submit">Save Notes</button>
    </form>
  );
};

export default App;
