import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const currentDate = new Date().toLocaleDateString();

  const [formValues, setFormValues] = useState([]);

  useEffect(() => {
    // Fetch data from API
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/');
        const apiData = response.data;

        console.log('API Data:', apiData);

        // Assuming your API response structure is an array of objects
        if (Array.isArray(apiData) && apiData.length > 0) {
          // Convert API data to the expected format
          const convertedData = apiData.map((item) => ({
            Note: item.notes || '',       // Use 'notes' property for Note
            DueDate: item.duedate || '',   // Use 'duedate' property for DueDate
            Date: item.date1 || currentDate,
          }));

          console.log('Converted Data:', convertedData);

          setFormValues(convertedData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [currentDate]);

  const handleChange = (i, e) => {
    let newFormValues = [...formValues];
    newFormValues[i][e.target.name] = e.target.value;
    setFormValues(newFormValues);
  };

  const addFormFields = () => {
    setFormValues([...formValues, { Note: '', DueDate: '', Date: currentDate }]);
  };

  const handleRemoveField = (i) => {
    let newFormValues = [...formValues];
    newFormValues.splice(i, 1);
    setFormValues(newFormValues);
  };
  const handleDeleteNote = async (i) => {
    try {
      const response = await axios.delete(`http://127.0.0.1:8000/api/notes/${i}`);
      console.log('Delete Note Response:', response.data);
      // Update formValues to remove the deleted note
      const newFormValues = [...formValues];
      newFormValues.splice(i, 1);
      setFormValues(newFormValues);
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const convertedData = formValues.map((element) => {
        let convertedDate;

        if (typeof element.Date === "string") {
          // Assume date is already in YYYY-MM-DD format
          convertedDate = element.Date;
        } else if (element.Date instanceof Date) {
          // Convert Date object to YYYY-MM-DD format
          convertedDate = element.Date.toISOString().substring(0, 10);
        } else {
          // Throw error if date format is not recognized
          throw new TypeError("Invalid date format. Expected String or Date object.");
        }

        // Convert other fields as needed
        const convertedNote = element.Note.toUpperCase();

        return {
          ...element,
          Date: convertedDate,
          Note: convertedNote,
        };
      });

      const response = await axios.post("http://127.0.0.1:8000/api/", convertedData);
      console.log("Save Notes Response:", response.data);
      setFormValues([]);
    } catch (error) {
      console.error("Error saving notes:", error);
    }
  };


  return (
    <form onSubmit={handleSubmit}>
      <h1><font size="+8">React Scribe</font></h1>
      <div className="span">
        <span>
          <button type="button" onClick={() => addFormFields()}>üìù</button>
          <button type="button">Feeling Lucky</button>
        </span>
      </div>
      <div className="span">
        <span></span>
      </div>
      <div className="notes-container">
        {formValues.map((element, index) => (
          <div className="note-container" key={index}>
            <label><strong>Date: </strong>{element.Date}</label>
            <textarea
              placeholder="Script destiny here! ‚úíÔ∏èüíÄ"
              cols="30"
              rows="10"
              type="text"
              name="Note"
              value={element.Note}
              onChange={(e) => handleChange(index, e)}
            ></textarea>
            <label><strong>Due Date: </strong></label>
            <input
              type="date"
              name="DueDate"
              value={element.DueDate}
              onChange={(e) => handleChange(index, e)}
              style={{
                borderWidth: '3px',
                borderStyle: new Date().getTime() >= new Date(element.DueDate).getTime() ? 'dotted' : 'none',
                borderColor: '#F4442E',
              }}
            />
            {index ? (
              <button type="button" onClick={() => handleRemoveField(index)}>üóëÔ∏è</button>
            ) : null}
          </div>
        ))}
      </div>
      <button type="submit">Save Notes</button>
      <div>
        <p>Debug Info:</p>
        <pre>{JSON.stringify(formValues, null, 2)}</pre>
      </div>
    </form>
  );
};

export default App;
