import './App.css';
import React, { useState, useEffect } from 'react';

const App = () => {
  const currentDate = new Date()

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [datePickerVisible, setDatePickerVisible] = useState(false);

  const showDatePicker = () => {
    setDatePickerVisible(true);
  };

  const hideDatePicker = () => {
    setDatePickerVisible(false);
  };

  const handleConfirm = (date) => {
    setSelectedDate(date);
    hideDatePicker();
  };

  const initialFormValues = JSON.parse(localStorage.getItem('formValues')) || [
    { Note: "", DueDate: '',  },
    { Note: "", DueDate: '',},
    { Note: "", DueDate: nextDay },
  ];


  const [formValues, setFormValues] = useState(initialFormValues);
  useEffect(() => {
    localStorage.setItem('formValues', JSON.stringify(formValues));
  }, [formValues, currentDate]);

  const handleChange = (i, e) => {
    let newFormValues = [...formValues];
    newFormValues[i][e.target.name] = e.target.value;
    setFormValues(newFormValues);
  };

  const addFormFields = () => {
    setFormValues([...formValues, { Note: "", DueDate: currentDate, Date: currentDate }]);
  };

  const handleRemoveField = (i) => {
    let newFormValues = [...formValues];
    newFormValues.splice(i, 1);
    setFormValues(newFormValues);
  };

  const handleSubmit = (event) => {

    alert(JSON.stringify('You suck at life'));


  };
  const getColor = (value) => {
    if (value > 70) {
      return 'green';
    } else if (value >= 30 && value <= 70) {
      return 'yellow';
    } else {
      return 'red';
    }
  };

  useEffect(() => {
    return () => {
      localStorage.removeItem('formValues');
    };
  }, []);

  return (
    <form onSubmit={handleSubmit}>
      <h1> <font size="+8">React Scribe</font></h1>
      <div className="span">
        <span>
          <button type="button" onClick={() => addFormFields()}>
          üìù
          </button>

          <a href="https://www.youtube.com/watch?v=dQw4w9WgXcQ">
          <button type="button" >
            Feeling Lucky
          </button>
</a>
        </span>
        </div>
        <div className="span">
        <span>

        </span>
      </div>
      <div className="notes-container">

        {formValues.map((element, index) => (
          <div className="note-container" key={index}>
            <label htmlFor={`textbox-${index}`}><strong><font size="+3">  </font></strong></label>
            <p>{element.Date}</p>

            <textarea

            placeholder="Script destiny here! ‚úíÔ∏èüíÄ"
              cols="30"
              rows="10"
              id={`textbox-${index}`}
              type="text"
              name="Note"
              value={element.Note}
              onChange={(e) => handleChange(index, e)}
              key={`textarea-${index}`}
            ></textarea>
            <label htmlFor={`dueDate-${index}`}><strong>Due Date</strong></label>


            {index ? (

              <button type="button" onClick={() => handleRemoveField(index)}>
                üóëÔ∏è
              </button>
            ) : null}


          </div>
        ))}
      </div>
    </form>
  );

};

export default App;
